using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;

public class ImgCoroutine : MonoBehaviour
{
	//ссылка на фото
    [SerializeField] private string url;
	//переменная для хранения текстуры
	Texture2D texture;

	//массив по запросу гугл "холодец фото"
	private string[] number = new string[] {"https://ist1.objorka.com/img0008/89/889_0151zrd_0217_6hi.jpg", "https://www.maggi.ru/data/images/recept/img640x500/recept_2355_81d4.jpg",
