	"https://img.povar.ru/main/08/98/88/2e/holodec_iz_baranini-154148.jpg", "https://kulinarnia.ru/wp-content/uploads/2018/12/holodec-iz-govyadiny.jpg", "https://www.povarenok.ru/data/cache/2013nov/21/37/568483_22245-710x550x.jpg",
	"https://www.koolinar.ru/all_image/recipes/140/140602/recipe_4d32f399-b9cd-484b-8b91-f12e4c31755a_large.jpg", "https://www.photorecept.ru/wp-content/uploads/2010/11/xolodec-iz-indejki-buxanochka-1300x931.jpg",
	"https://chudo-povar.com/images/holodec-s-zhelatinom-recepty-s-foto-1.jpg", "https://static.1000.menu/img/content/23163/xolodec-iz-rulki-i-govyadiny_1508737539_1_max.jpg", "https://the-challenger.ru/wp-content/uploads/2017/12/shutterstock_119348425-800x533.jpg", 
	"https://rutxt.ru/files/10823/original/8cba4a11f9.JPG", "https://www.gotovim.ru/pics/fotorecs/rec2519.jpg", "https://hlebopechka.ru/forum/result-photo/510116.jpg", "https://infoeda.com/wp-content/uploads/2019/11/Holodec-iz-kuricy-s-zhelatinom.jpg", 
	"https://www.gastronom.ru/binfiles/images/20151110/b1ea9c4f.jpg", "https://cs9.pikabu.ru/post_img/big/2019/12/12/8/1576157920139664058.jpg", "https://recipes.av.ru//media/recipes/100705_picture_DjEue7o.jpg",  
	"https://2eda.ru/files/143/original/0933a084f8.JPG", "https://povarixa.ru/images/photos/medium/article540.jpg", "https://vilkin.pro/wp-content/uploads/2019/03/holodec-iz-kurici-s-zelatinom-770x513.jpg", "https://xcook.info/wp-content/uploads/sites/default/files/styles/large/public/Holodec-iz-petuha.jpg", 
	"https://e3.edimdoma.ru/data/recipes/0003/3379/33379-ed4_wide.jpg?1468656762", "https://kakvarim.ru/wp-content/uploads/2018/07/kak-varit-xolodec-post-800x500.jpg", "https://www.vkussovet.ru/content/media/pic/large/1000000/7000/6081-b4fc655c123846d2.jpg", 
	"https://www.gastronom.ru/binfiles/images/20180114/b45fc4b2.jpg", "https://upload.wikimedia.org/wikipedia/commons/9/9c/GelatinaDiMaiale.JPG", "https://xcook.info/wp-content/uploads/sites/default/files/styles/large/public/Holodec-iz-kuricy-8.jpg", 
	"https://static.1000.menu/img/content/29235/xolodec-iz-utki_1540310667_1_max.jpg", "https://gotovim-doma.org/wp-content/uploads/2019/08/9-4.jpg", "https://km-doma.ru/assets/gallery_thumbnails/c8/c8317f3d2bbe3e696b41d35cfcf00dbc.jpg"};
    
    //назначаем текстуры картинам на старте 
	void Start()
    {
        StartCoroutine(NewURL());
    }

	//корутина изменения текстуры
    private IEnumerator NewURL()
	{
		//выбор случайного изображения
		url = number[Random.Range(0, number.Length-1)];
		//начало загрузки
		var www = new WWW(url);

		//ожидание конца загрузки
		yield return www; 

		//создание текстуры
		texture = new Texture2D(www.texture.width, www.texture.height, TextureFormat.DXT1, false);
		
		www.LoadImageIntoTexture(texture);
		Rect rect = new Rect(0, 0, texture.width, texture.height);
		Sprite sprite = Sprite.Create(texture, rect, new Vector2(0.5f, 0.5f), 100);

		GetComponent<Image>().sprite = sprite;

	}

	//функция запуска корутины изменения текстуры
    public void SC()
    {
        StartCoroutine(NewURL());
    }
}
